// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
import {IMarket} from "../interfaces/market/IMarket.sol";
import {IPositionStore} from "../interfaces/position/IPositionStore.sol";
import {IFastPriceFeed} from "../interfaces/oracle/IFastPriceFeed.sol";
import {IPrice} from "../interfaces/oracle/IPrice.sol";
import {IChainPriceFeed} from "../interfaces/oracle/IChainPriceFeed.sol";
import {IMarketValid} from "../interfaces/market/IMarketValid.sol";

library AMLib {
    function ps(
        address market,
        bool isLong
    ) internal view returns (IPositionStore) {
        return
            IPositionStore(
                isLong
                    ? IMarket(market).positionBook().longStore()
                    : IMarket(market).positionBook().shortStore()
            );
    }

    function fp(address market) internal view returns (IFastPriceFeed) {
        return
            IFastPriceFeed(IPrice(IMarket(market).priceFeed()).fastPriceFeed());
    }

    function cp(address market) internal view returns (IChainPriceFeed) {
        return
            IChainPriceFeed(
                IPrice(IMarket(market).priceFeed()).chainPriceFeed()
            );
    }

    function mv(address market) internal view returns (IMarketValid) {
        return IMarketValid(IMarket(market).marketValid());
    }
}

